class App extends React.Component {

 constructor(props) {
    super(props);
    this.state = {value: '',
                  url: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    console.log("making request")
    fetch('/result')
      .then(response => {
        console.log(response)
        return response.json()
      })
      .then(json => {
      console.log=(json)
      this.setState({url: json[0]})
      })
  }



   render() {
    // const recycleStyle = {
    //   backgroundColor: 'blue',
    // };
    return (
      <div>
        <p>Recycle Sort</p>

        <form onSubmit={this.handleSubmit} action="http://localhost:5000/result" method="get">
        <label>
          URL to Classify
          <input type="text" name="url"/>
          <p>
            <button type="submit" class="button" value="Submit" onChange={this.handleChange} value={this.state.value}>Submit</button>  
          </p>
        </label>
        </form>

      </div>
    );
  }
}


ReactDOM.render(<App />, document.getElementById('root'));
export default App;
